// Login.js
import React, { useState, useContext } from "react";
import Navbar from "../components/navbar";
import "../assets/css/login.css";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "AuthContext"; // Importa el contexto

const Login = () => {
  const [correo, setCorreo] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const navigate = useNavigate();
  const { setIsAuthenticated } = useContext(AuthContext); // Obtener el contexto

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Formulario enviado");

    const credentials = {
      CORREO: correo,
      CONTRASENA: contrasena
    };

    fetch('http://localhost:8080/api/v1/admin/tablaUsuarios/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    })
        .then(response => {
          if (!response.ok) {
            throw new Error('Credenciales inválidas');
          }
          return response.json(); // Espera JSON aquí
        })
        .then(data => {
          console.log('Login exitoso:', data);
          const token = data.token; // Asegúrate de que el nombre coincide con tu API
          localStorage.setItem('token', token); // Almacena el token
          setIsAuthenticated(true); // Cambia el estado de autenticación
          navigate('/cita');
        })
        .catch((error) => {
          setMensaje(error.message);
          console.error('Error:', error);
        });
  };

  return (
      <div className="back">
        <Navbar />
        <div className="div-center">
          <div className="content ">
            <h3 className="center text-center">
              BIENVENIDO AL PORTAL DE
              <br />
              BIBLIOTECA UPB
            </h3>
            <hr />
            <form onSubmit={handleSubmit}>
              <div className="form-group text-center">
                <label htmlFor="exampleInputEmail1">Correo electrónico</label>
                <input
                    type="email"
                    value={correo}
                    onChange={(e) => setCorreo(e.target.value)}
                    className="form-control"
                    id="exampleInputEmail1"
                    placeholder="Correo electrónico"
                />
              </div>
              <div className="form-group text-center">
                <label htmlFor="exampleInputPassword1">Contraseña</label>
                <input
                    type="password"
                    value={contrasena}
                    onChange={(e) => setContrasena(e.target.value)}
                    className="form-control"
                    id="exampleInputPassword1"
                    placeholder="*****"
                />
              </div>
              <div className="row align-items-start my-3 mx-1">
                <button type="submit" className="col btn btn-danger">
                  Iniciar sesión
                </button>
              </div>
              <hr />
            </form>
            {mensaje && <p className="text-danger">{mensaje}</p>}
          </div>
        </div>
      </div>
  );
};

export default Login;
