import React, { useState } from 'react';
import '../assets/css/accordion.css';

const Accordion = () => {
    // Estado que guarda el índice del acordeón abierto (null si ninguno está abierto)
    const [activeIndex, setActiveIndex] = useState(null);

    // Función para alternar el acordeón
    const toggleAccordion = (index) => {
        setActiveIndex(activeIndex === index ? null : index); // Cerrar si está abierto, abrir si está cerrado
    };

    const accordionItems = [
        {
            title: 'Item 1',
            content: 'Contenido del acordeón 1. Puedes colocar cualquier información aquí.'
        },
        {
            title: 'Item 2',
            content: 'Contenido del acordeón 2. Puedes colocar cualquier información aquí.'
        },
        {
            title: 'Item 3',
            content: 'Contenido del acordeón 3. Puedes colocar cualquier información aquí.'
        }
    ];

    return (
        <div className="accordion-container">
            {accordionItems.map((item, index) => (
                <div key={index} className="accordion-item">
                    <div
                        className="accordion-title"
                        onClick={() => toggleAccordion(index)}
                    >
                        <h3>{item.title}</h3>
                        <span>{activeIndex === index ? '-' : '+'}</span>
                    </div>
                    <div className={`accordion-content ${activeIndex === index ? 'open' : ''}`}>
                        <p>{item.content}</p>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Accordion;
