import React, { useState } from "react";
import Navbar from "../components/navbar";
import { useAuth } from "../AuthContext"; // Asegúrate de que la ruta sea correcta
import "../assets/css/login.css";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [correo, setCorreo] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth(); // Obtener la función de login del contexto

  const handleSubmit = (e) => {
    e.preventDefault();

    const credentials = {
      CORREO: correo,
      CONTRASENA: contrasena
    };

    fetch('http://localhost:8080/api/v1/admin/tablaUsuarios/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    })
        .then(response => {
          if (!response.ok) {
            return response.json().then(errData => {
              throw new Error(errData.message || 'Credenciales inválidas');
            });
          }
          return response.json(); // Cambia a json si esperas un objeto JSON
        })
        .then(data => {
          login(data); // Llamar a la función de login para guardar el usuario
          navigate('/cita');
        })
        .catch(error => {
          setMensaje(error.message);
          console.error('Error:', error);
        });
  };

  return (
      <div className="back">
        <Navbar />
        <div className="div-center">
          <div className="content">
            <h3 className="center text-center">
              BIENVENIDO AL PORTAL DE
              <br />
              BIBLIOTECA UPB
            </h3>
            <hr />
            <form onSubmit={handleSubmit}>
              <div className="form-group text-center">
                <label htmlFor="exampleInputEmail1">Correo electrónico</label>
                <input
                    type="email"
                    value={correo}
                    onChange={(e) => setCorreo(e.target.value)}
                    className="form-control"
                    id="exampleInputEmail1"
                    placeholder="Correo electrónico"
                />
              </div>
              <div className="form-group text-center">
                <label htmlFor="exampleInputPassword1">Contraseña</label>
                <input
                    type="password"
                    value={contrasena}
                    onChange={(e) => setContrasena(e.target.value)}
                    className="form-control"
                    id="exampleInputPassword1"
                    placeholder="*****"
                />
              </div>
              <div className="row align-items-start my-3 mx-1">
                <button type="submit" className="col btn btn-danger">
                  Iniciar sesión
                </button>
              </div>
              <hr />
            </form>
            {mensaje && <p className="text-danger">{mensaje}</p>}
          </div>
        </div>
      </div>
  );
};

export default Login;
