import React, { useState } from "react";
import Navbar from "../components/navbar";
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';

const See_Appointments = () => {
  const [citasPendientes, setCitasPendientes] = useState([
    {
      id: 1,
      tipoCita: 'Asesoría Legal',
      tipoUsuario: 'Cliente',
      nombre: 'Juan Pérez',
      profesion: 'Abogado',
      virtual: 'Sí',
      descripcion: 'Consulta sobre contratos.',
    },
  ]);

  const [citasAprobadas, setCitasAprobadas] = useState([
    {
      id: 2,
      tipoCita: 'Asesoría Financiera',
      tipoUsuario: 'Cliente',
      nombre: 'Maria López',
      profesion: 'Contadora',
      virtual: 'No',
      descripcion: 'Revisión de estados financieros.',
    },
  ]);

  // Aprobar cita: mueve de pendientes a aprobadas
  const handleAprobarCita = (citaId) => {
    const citaAprobada = citasPendientes.find((cita) => cita.id === citaId);
    if (citaAprobada) {
      setCitasAprobadas([...citasAprobadas, citaAprobada]);
      setCitasPendientes(citasPendientes.filter((cita) => cita.id !== citaId));
    }
  };

  // Negar cita: elimina la cita
  const handleNegarCita = (citaId) => {
    Swal.fire({
      title: 'Ingrese motivo de cancelación',
      input: 'textarea',
      inputLabel: 'Motivo',
      inputPlaceholder: 'Escribe el motivo aquí...',
      showCancelButton: true,
      confirmButtonText: 'Enviar',
      cancelButtonText: 'Cancelar',
    }).then((result) => {
      if (result.isConfirmed) {
        setCitasPendientes(citasPendientes.filter((cita) => cita.id !== citaId));
        setCitasAprobadas(citasAprobadas.filter((cita) => cita.id !== citaId));
        Swal.fire('Cita cancelada', `Motivo: ${result.value}`, 'success');
      }
    });
  };

  return (
      <div>
        <Navbar />
        <h3 className="text-center my-4">VER CITAS</h3>
        <div className="container">
          <div className="row justify-content-around">

            {/* Columna de citas pendientes */}
            <div className="col-12 col-md-5 pb-3 mb-2" style={{ backgroundColor: "lightblue" }}>
              <h4 className="text-center py-2">Citas pendientes</h4>
              <div className="accordion" id="accordionPending">
                {citasPendientes.map((cita) => (
                    <div className="accordion-item" key={cita.id}>
                      <h2 className="accordion-header" id={`headingPending${cita.id}`}>
                        <button
                            className="accordion-button collapsed"
                            type="button"
                            data-bs-toggle="collapse"
                            data-bs-target={`#collapsePending${cita.id}`}
                            aria-expanded="false"
                            aria-controls={`collapsePending${cita.id}`}
                        >
                          {`Tipo de cita: ${cita.tipoCita}, Tipo de usuario: ${cita.tipoUsuario}`}
                        </button>
                      </h2>
                      <div
                          id={`collapsePending${cita.id}`}
                          className="accordion-collapse collapse"
                          aria-labelledby={`headingPending${cita.id}`}
                          data-bs-parent="#accordionPending"
                      >
                        <div className="accordion-body">
                          <p><strong>Tipo de asesoría:</strong> {cita.tipoCita}</p>
                          <p><strong>Nombre:</strong> {cita.nombre}</p>
                          <p><strong>Profesión:</strong> {cita.profesion}</p>
                          <p><strong>Virtual:</strong> {cita.virtual}</p>
                          <p><strong>Descripción:</strong> {cita.descripcion}</p>
                          <div className="d-flex justify-content-between mt-3">
                            <button className="btn btn-success" onClick={() => handleAprobarCita(cita.id)}>Aprobar</button>
                            <button className="btn btn-danger" onClick={() => handleNegarCita(cita.id)}>Negar cita</button>
                          </div>
                        </div>
                      </div>
                    </div>
                ))}
              </div>
            </div>

            {/* Columna de citas aprobadas */}
            <div className="col-12 col-md-5 pb-3 mb-2" style={{ backgroundColor: "lightgreen" }}>
              <h4 className="text-center py-2">Citas aprobadas</h4>
              <div className="accordion" id="accordionApproved">
                {citasAprobadas.map((cita) => (
                    <div className="accordion-item" key={cita.id}>
                      <h2 className="accordion-header" id={`headingApproved${cita.id}`}>
                        <button
                            className="accordion-button collapsed"
                            type="button"
                            data-bs-toggle="collapse"
                            data-bs-target={`#collapseApproved${cita.id}`}
                            aria-expanded="false"
                            aria-controls={`collapseApproved${cita.id}`}
                        >
                          {`Tipo de cita: ${cita.tipoCita}, Tipo de usuario: ${cita.tipoUsuario}`}
                        </button>
                      </h2>
                      <div
                          id={`collapseApproved${cita.id}`}
                          className="accordion-collapse collapse"
                          aria-labelledby={`headingApproved${cita.id}`}
                          data-bs-parent="#accordionApproved"
                      >
                        <div className="accordion-body">
                          <p><strong>Tipo de asesoría:</strong> {cita.tipoCita}</p>
                          <p><strong>Nombre:</strong> {cita.nombre}</p>
                          <p><strong>Profesión:</strong> {cita.profesion}</p>
                          <p><strong>Virtual:</strong> {cita.virtual}</p>
                          <p><strong>Descripción:</strong> {cita.descripcion}</p>
                          {/* No se muestran botones de confirmar o negar para citas aprobadas */}
                        </div>
                      </div>
                    </div>
                ))}
              </div>
            </div>

          </div>
        </div>
      </div>
  );
};

export default See_Appointments;
