import React, { useState } from "react";
import Navbar from "../components/navbar"; // Asegúrate de que la ruta sea correcta
import "../assets/css/login.css";
import { useNavigate } from "react-router-dom"; // Importar useNavigate

const Login = () => {
  const [correo, setCorreo] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const navigate = useNavigate(); // Usar useNavigate para redireccionar

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Formulario enviado"); // Para verificar que se ha enviado el formulario

    const credentials = {
      CORREO: correo,        // Asegúrate de que los nombres coincidan con los que espera tu API
      CONTRASENA: contrasena
    };

    fetch('http://localhost:8080/api/v1/admin/tablaUsuarios/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    })
        .then(response => {
          console.log(response); // Para verificar la respuesta de la API
          if (response.status === 401) {
            throw new Error('Credenciales inválidas');
          } else if (!response.ok) {
            throw new Error('Error en la solicitud');
          }
          return response.json(); // Cambia esto si esperas un formato diferente
        })
        .then(data => {
          setMensaje(data); // Guarda el mensaje que devuelve la API
          console.log('Login exitoso:', data);
          navigate('/cita'); // Redireccionar a /cita después de un login exitoso
        })
        .catch((error) => {
          setMensaje(error.message);
          console.error('Error:', error);
        });
  };

  return (
      <div className="back">
        <Navbar />
        <div className="div-center">
          <div className="content ">
            <h3 className="center text-center">
              BIENVENIDO AL PORTAL DE
              <br />
              BIBLIOTECA UPB
            </h3>
            <hr />
            <form onSubmit={handleSubmit}>
              <div className="form-group text-center">
                <label htmlFor="exampleInputEmail1">Correo electrónico</label>
                <input
                    type="email"
                    value={correo}
                    onChange={(e) => setCorreo(e.target.value)} // Manejar el cambio
                    className="form-control"
                    id="exampleInputEmail1"
                    placeholder="Correo electrónico"
                    required // Añadir required para validar el campo
                />
              </div>
              <div className="form-group text-center">
                <label htmlFor="exampleInputPassword1">Contraseña</label>
                <input
                    type="password"
                    value={contrasena}
                    onChange={(e) => setContrasena(e.target.value)} // Manejar el cambio
                    className="form-control"
                    id="exampleInputPassword1"
                    placeholder="*****"
                    required // Añadir required para validar el campo
                />
              </div>
              <div className="row align-items-start my-3 mx-1">
                <button type="submit" className="col btn btn-danger">
                  Iniciar sesión
                </button>
              </div>
              <hr />
            </form>
            {mensaje && <p className="text-danger">{mensaje}</p>} {/* Mostrar mensajes */}
          </div>
        </div>
      </div>
  );
};

export default Login;
