import React, { useState } from "react";
import Navbar from "../components/navbar";
import { useAuth } from "../AuthContext"; // Asegúrate de que la ruta sea correcta
import "../assets/css/login.css";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [correo, setCorreo] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensaje(''); // Limpiar mensaje previo

    const credentials = {
      CORREO: correo,
      CONTRASENA: contrasena
    };

    try {
      const response = await fetch('http://localhost:8080/api/v1/admin/tablaUsuarios/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      // Verificar si la respuesta es un error
      if (!response.ok) {
        // Si la respuesta no es ok, leer el mensaje de error
        const textData = await response.text(); // Obtener los datos de error como texto
        setMensaje(textData || 'Credenciales inválidas'); // Establecer el mensaje de error
        return; // Salir de la función
      }

      // Si la respuesta es correcta, obtener los datos como JSON
      const data = await response.json();
      // Almacenar los datos en localStorage
      localStorage.setItem('user', JSON.stringify(data)); // Guardar el usuario en localStorage
      login(data); // Llamar a la función de login para guardar el usuario
      navigate('/cita');

    } catch (error) {
      setMensaje('Error en la conexión. Inténtalo de nuevo más tarde.'); // Mensaje genérico
      console.error('Error:', error);
    }
  };

  return (
      <div className="back">
        <Navbar />
        <div className="div-center">
          <div className="content">
            <h3 className="center text-center">
              BIENVENIDO AL PORTAL DE
              <br />
              BIBLIOTECA UPB
            </h3>
            <hr />
            <form onSubmit={handleSubmit}>
              <div className="form-group text-center">
                <label htmlFor="exampleInputEmail1">Correo electrónico</label>
                <input
                    type="email"
                    value={correo}
                    onChange={(e) => setCorreo(e.target.value)}
                    className="form-control"
                    id="exampleInputEmail1"
                    placeholder="Correo electrónico"
                    required
                />
              </div>
              <div className="form-group text-center">
                <label htmlFor="exampleInputPassword1">Contraseña</label>
                <input
                    type="password"
                    value={contrasena}
                    onChange={(e) => setContrasena(e.target.value)}
                    className="form-control"
                    id="exampleInputPassword1"
                    placeholder="*****"
                    required
                />
              </div>
              <div className="row align-items-start my-3 mx-1">
                <button type="submit" className="col btn btn-danger">
                  Iniciar sesión
                </button>
              </div>
              <hr />
            </form>
            {mensaje && <p className="text-danger">{mensaje}</p>} {/* Mostrar mensaje de error */}
          </div>
        </div>
      </div>
  );
};

export default Login;
